ls -l $dir | tail -n+2 | tr -s ' ' | sed -r 's/^-/File /' |sed -r 's/^d/Directory /' | cut -d' ' -f1,7-10 | sed -r 's/[[:digit:]]{2}:[[:digit:]]{2}/2019/' | sed -r 's/((File|Directory)) ([[:upper:]][[:lower:]]{2}) ([[:digit:]]{1,2}) ([[:digit:]]{4}) (.*$)/\1 \6 last updated on \3 \4 \5/' | sed -r 's/ ((1|21|31)) / \1st /' | sed -r 's/ ((2|22)) / \1nd /' | sed -r 's/ ((3|23)) / \1rd /' | sed -r 's/ ([[:digit:]]{1,2}) / \1th /'
